export class Book {
    private _title: string;
    private _author: string;
    private _description: string;
    private _image: string;
    private _tags: string[];
    private _status: string;
    private _requester: string;
    private _id?: string;

    constructor(title: string, author: string, description: string, image: string, tags: string[], status: string, requester: string, id?: string) {
      this._title = title;
      this._author = author;
      this._description = description;
      this._image = image;
      this._tags = tags;
      this._status = status;
      this._requester = requester;
      this._id = id;
    }

    get title(): string {
      return this._title;
    }

    set title(value: string) {
      this._title = value;
    }

    get author(): string {
      return this._author;
    }

    set author(value: string) {
      this._author = value;
    }

    get description(): string {
      return this._description;
    }

    set description(value: string) {
      this._description = value;
    }

    get image(): string {
      return this._image;
    }

    set image(value: string) {
      this._image = value;
    }

    get tags(): string[] {
      return this._tags;
    }

    set tags(value: string[]) {
      this._tags = value;
    }

    get status(): string {
      return this._status;
    }

    set status(value: string) {
      this._status = value;
    }

    get requester(): string {
      return this._requester;
    }

    set requester(value: string) {
      this._requester = value;
    }

    get id(): string | undefined {
      return this._id;
    }

    set id(value: string | undefined) {
      this._id = value;
    }

    hasTag(tag: string): boolean {
        return this._tags.includes(tag);
    }

    toJSON(): any {
        return {
          title: this.title,
          author: this.author,
          description: this.description,
          image: this.image,
          tags: this.tags,
          status: this.status,
          requester: this.requester,
        };
    }
  }

import { Injectable } from '@angular/core';
import {collection, collectionData, Firestore, query } from '@angular/fire/firestore';
import { University } from '../models/university';
import { map, Observable } from 'rxjs';
import { orderBy } from 'firebase/firestore';

@Injectable({
  providedIn: 'root'
})
export class UniversityService {
  constructor(private firestore: Firestore) { }

  getUniversities(): Observable<University[]> {
    const universityCollectionRef = query(collection(this.firestore, "universities"), orderBy('name'));
    return collectionData(universityCollectionRef, { idField: 'id' })
            .pipe(
              map(universities => {
                return universities as University[];
              })
            )
  }
}

import { Injectable } from '@angular/core';
import { collection, collectionData, Firestore, addDoc, doc, updateDoc, docData, query, where, deleteDoc, orderBy } from '@angular/fire/firestore';
import { map, Observable } from 'rxjs';
import { Book } from '../models/book';

@Injectable({
  providedIn: 'root'
})
export class BookService {

  constructor(
    private firestore: Firestore,
    ) {}

  getBooks(status?: string, requester?: string): Observable<Book[]> {
    let q;
    if (status !== undefined) {
      q = query(collection(this.firestore, "books"), where("status", "==", status), orderBy('title'));
    } else if (requester !== undefined) {
      q = query(collection(this.firestore, "books"), where("requester", "==", requester), orderBy('title'));
    } else {
      q = query(collection(this.firestore, "books"), orderBy('title'));
    }
    return collectionData(q, { idField: 'id' })
      .pipe(
        map(books => books.map(book => new Book(book['title'], book['author'], book['description'], book['image'], book['tags'], book['status'], book['requester'], book['id'])))
      );
  }

  getBook(bookId: string): Observable<Book> {
    const bookDocumentRef = doc(this.firestore, 'books', bookId)
    return docData(bookDocumentRef, { idField: 'id'})
            .pipe(
              map(book => new Book(book['title'], book['author'], book['description'], book['image'], book['tags'], book['status'], book['requester'], book['id']))
            )
  }

  async createBook(book: Book): Promise<void> {
    const booksCollectionRef = collection(this.firestore, 'books');
    await addDoc(booksCollectionRef, book.toJSON());
  }

  async updateBook(bookId: string, data): Promise<void> {
    const bookDocumentRef = doc(this.firestore, 'books', bookId);
    await updateDoc(bookDocumentRef, data);
  }

  async deleteBook(bookId: string): Promise<void> {
    const bookDocumentRef = doc(this.firestore, 'books', bookId);
    await deleteDoc(bookDocumentRef);
  }

}

