      if(tag) {
        if(this.book.hasTag(tag)) {
          this.snackBar.open('Tag already exists', 'Dismiss', {
            panelClass: 'warning',
        })
        } else {
          const data = {
            tags: arrayUnion(tag)
          } 
          try {
            this.bookService.updateBook(this.book.id, data);
            this.snackBar.open('Tag recommended', 'Dismiss', {
              panelClass: 'success',
              duration: 5000,
            })
          }
          catch(error) {
            this.snackBar.open(error, 'Dismiss', {
              panelClass: 'error',
              duration: 5000,
            })
          }
        }
      }
    });
  }

  getAvgRating(value: number) {
    this.avgRating = value;
  }

  ngOnDestroy(): void {
    this.bookSubscription.unsubscribe();
